// --- Variables del producto ---
const timestamp = Date.now();
const nombreProducto = `P_Inventariable_${timestamp}`;
const cantidadProducto = "22";
const precioCompra = "50000";
const precioVenta = "60000";
const descripcion = "Producto de prueba con inventario";
// --- Navegar a la página de Productos ---
cy.visit("/productos");
// --- Validar que estamos en la página de Productos ---
cy.url().should("include", "/productos");
// --- Abrir modal de "Agregar producto" ---
cy.get('button.btn-agregar[data-target="#ingresoModal"]').should("be.visible").click();
cy.get("#ingresoModal").should("be.visible");
// --- Verificar que el switch esté marcado como inventariable por defecto ---
cy.get('#inventariableSwitch').should("be.checked");
cy.get('#tipoTexto').should("contain", "Inventariable");
// --- Llenar datos del producto ---
cy.get('#ingresoModal input[name="nombreProducto"]').type(nombreProducto);
// Código se selecciona sin escribir
cy.get('#ingresoModal input[name="codigoProducto"]').click();
cy.get("#sugerencia-codigo").should("exist");
// Descripción del producto
cy.get('#ingresoModal textarea[name="descripcion"]').type(descripcion);
// Cantidad (solo para inventariables)
cy.get('#ingresoModal input[name="cantidadP"]').type(cantidadProducto);
// Precios
cy.get('#ingresoModal input[name="precioCompra_formatted"]').type(precioCompra);
cy.get('#ingresoModal input[name="precioProducto_formatted"]').type(precioVenta);
// --- Guardar producto ---
cy.get('#ingresoModal button.btn.btn-primary[type="submit"]').click();
// --- Validar cierre de modal ---
cy.get("#ingresoModal").should("not.be.visible");
// --- Validar que el producto aparece en la tabla ---
cy.contains(nombreProducto).should("be.visible");

// --- Variables del producto ---
const timestamp = Date.now();
const nombreProducto = `P_NoInventariable_${timestamp}`;
const precioCompra = "30000";
const precioVenta = "45000";
const descripcion = "Producto de prueba sin inventario (servicio)";
// --- Navegar a la página de Productos ---
cy.visit("/productos");
// --- Validar que estamos en la página de Productos ---
cy.url().should("include", "/productos");
// --- Abrir modal de "Agregar producto" ---
cy.get('button.btn-agregar[data-target="#ingresoModal"]').should("be.visible").click();
cy.get("#ingresoModal").should("be.visible");
// --- Desactivar el switch de inventariable ---
cy.get('#inventariableSwitch').uncheck();
// --- Verificar que el tipo cambió a No Inventariable ---
cy.get('#inventariableSwitch').should("not.be.checked");
cy.get('#tipoTexto').should("contain", "No Inventariable");
cy.get('#tipoDescripcion').should("contain", "No se creará inventario ni lotes");
// --- Llenar datos del producto ---
cy.get('#ingresoModal input[name="nombreProducto"]').type(nombreProducto);
// Código se selecciona sin escribir
cy.get('#ingresoModal input[name="codigoProducto"]').click();
cy.get("#sugerencia-codigo").should("exist");
// Descripción del producto
cy.get('#ingresoModal textarea[name="descripcion"]').type(descripcion);
// --- NO llenar cantidad porque es un producto no inventariable ---
// El campo debe tener valor 0 automáticamente
