cy.get('#ingresoModal input[name="precioCompra_formatted"]').type(precioCompra);
cy.get('#ingresoModal input[name="precioProducto_formatted"]').type(precioVenta);
// --- Guardar producto ---
cy.get('#ingresoModal button.btn.btn-primary[type="submit"]').click();
// --- Validar cierre de modal ---
cy.get("#ingresoModal").should("not.be.visible");
// --- Validar que el producto aparece en la tabla ---
cy.contains(nombreProducto).should("be.visible");
