// --- Variables del producto a editar ---
const nuevoCodigo = "COD-EDIT-001";
const nuevoNombre = "Producto Editado Test";
const nuevaDescripcion = "Este es un producto editado desde Cypress";
const nuevoPrecio = "25000";
// --- Abrir menú Gestión Financiera ---
cy.visit("/productos");
// --- Validar que estamos en la página de Productos ---
cy.url().should("include", "/productos");
// --- Abrir el primer modal de edición disponible ---
cy.get('button[data-target^="#productoModalEdit-"]').first().click();
// --- Trabajar dentro del modal ---
cy.get("div.modal.fade.show:visible").within(() => {
  // --- Editar código ---
  cy.get("input[id^='editCodigoProducto']").should("be.visible").click().clear().type(nuevoCodigo, {
    delay: 50
  });
  // --- Editar nombre ---
  cy.get("input[id^='editNombreProducto']").should("be.visible").click().clear().type(nuevoNombre);
  // --- Editar descripción ---
  cy.get("textarea[id^='editDescripcion']").should("be.visible").click().clear().type(nuevaDescripcion);
  // --- Editar precio ---
  cy.get("input[id^='precioProductoEdit']").should("be.visible").click().clear().type(nuevoPrecio);
  // --- Guardar cambios ---
  cy.get("div.modal-footer button.btn.btn-primary").should("be.visible").click();
});
// --- Espera a que el modal cierre correctamente ---
cy.get("div.modal.fade.show").should("not.exist");
